coef = {"a" : 0,        "b" : 0,        "c" : 0}print("Equation: y = ax^2 + bx + c")print("Enter a coefficient")coef["a"] = float(input())print("Enter b coefficient")coef["b"] = float(input())print("Enter c coefficient")coef["c"] = float(input())delta = coef["b"]**2 - 4 * coef["a"] * coef["c"]if delta > 0:    print("There are two roots.")    x1 = (-coef["b"] + delta ** 0.5)/(2.0 * coef["a"])    x2 = (-coef["b"] - delta ** 0.5) / (2.0 * coef["a"])    print("x1: " + str(x1) + "\nx2: " + str(x2))elif delta == 0:    print("There is one root.")    x0 = -coef["b"]/(2.0 * coef["a"])    print("x0: " + str(x0))else:    print("There are no roots.")